import React from 'react';

const Card = (props) => {

    // const handleCardClick = (card) => {
    //     onCardClick(card);
    //     console.log(card)
    // };

    function handleCardClick() {
        props.onCardClick(props.card)
        console.log(props.card)
    }


    return (
        <div className="photo-grid__item">
            <button type="button" className="photo-grid__show-btn" onClick={handleCardClick}>
                <img className="photo-grid__image" src={props.card.link} alt={props.card.name}/>
            </button>
            <div className="photo-grid__info">
                <h2 className="photo-grid__title">{props.card.name}</h2>
                <div className="photo-grid__like-container">
                    <button type="button" className="photo-grid__like"></button>
                    <span className="photo-grid__like-counter">{props.card.likes.length}</span>
                </div>
                <button type="button" className="photo-grid__delete"></button>
            </div>
        </div>
    )
}

export default Card;
